import React from 'react';
import './App.css';
import Numerics from "./components/Numerics"
import Result from "./components/Result"

class App extends React.Component{

  constructor(){
    super();
    this.setValue = this.setValue.bind(this);
    this.state ={
      string:"",
      equal:""
    };
}
  setValue(key){
    console.log(key)

    if(key === "="){
      let total = eval(this.state.string);
      this.setState({ equal :total})
    }
    else{
      let prev = this.state.result + key;
      this.setState({result :prev })
    }


  }
  render(){
    return (
      <div>
        <Result string={this.state.string} total={this.state.equal}></Result>
        <Numerics setValue={this.setValue}></Numerics>
      </div>
    )
  }
}
export default App;
